        <div className="col">
          <input className="form-control form-control-lg" />
        </div>

        <div className="col-auto">
          <button className="btn btn-lg btn-success mr-20">Atualizar</button>
          <button className="btn btn-lg btn-warming">Cancelar</button>
        </div>
      </div>



      {/* Adicionar Tarefa */}
      <div className="row">
        <div className="col">
          <input value={newTask}
          onChange={ (e) => setNewTask(e.target.value)}

          className="form-control form-control-lg" />
        </div>

        <div className="col-auto">
          <button 
          onClick={addTask}
