import React, {useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleCheck, faPen, faTrashCan } from '@fortawesome/free-solid-svg-icons'
import './App.css';


function App() {

  //Tarefas estrutura
  const [ToDo, setToDo] = useState([
    {"id": 1, "title": "Task 1", "status": false},
    {"id": 2, "title": "Task 2", "status": false},
  ])

  //State temporario
  const [newTask, setNewTask] = useState('');
  const [updateData, setUpdateData] = useState('');

  //Adicionar uma tarefa
  const addTask = () => {
    if(newTask) {
      let num = ToDo.length +1;
      let newEntry = { id: num, title: newTask, status: false }
      setToDo([...ToDo, newEntry])
      setNewTask('');
    }
  }

  //Deletar uma tarefa
  const deleteTask = (id) => {
    let newTasks = ToDo.filter( task => task.id !== id)
    setToDo(newTasks);
  }

  //Marcar tarefa como feito
  const markDone = (id) => {
    let newTask = ToDo.map( task => {
      if (task.id === id) {
        return ({...task, status: !task.status})
      }
      return task;
    })
    setToDo(newTask);
  }

  //Cancelar alteração
  const cancelUpdate = (id) => {
    setUpdateData('');
  }

  //Alterar tarefa
  const changeTask = (e) => {
    let newEntry = {
      id: updateData.id,
      title: e.target.value,
      status: updateData.status ? true : false
    }
    setUpdateData(newEntry);
  }

  //Atualizar tarefa
  const updateTask = () => {
    let filterRecords = [...ToDo].filter( task => task.id !== updateData.id );
    let updatedObject = [...filterRecords, updateData]
    setToDo(updatedObject);
    setUpdateData('');
  }




  return (
    <div className="container App">

      <h2>To-do List WhatsMenu</h2>

      {/* Atualizar Tarefa */}
      {updateData && updateData ? (
        <>
        <div className="row">
          <div className="col">
            <input
              value={ updateData && updateData.title }
              onChange={(e) => changeTask(e)}
              className="form-control form-control-lg" />
          </div>

          <div className="col-auto">
            <button
              onClick={updateTask}
              className="btn btn-lg btn-success mr-20">Atualizar</button>
            <button className="btn btn-lg btn-warming">Cancelar</button>
          </div>
        </div>
        </>

      ) : (

        <>        
          {/* Adicionar Tarefa */}
          <div className="row">
            <div className="col">
              <input value={newTask}
              onChange={ (e) => setNewTask(e.target.value)}

              className="form-control form-control-lg" />
            </div>

            <div className="col-auto">
              <button 
              onClick={addTask}
              className="btn btn-lg btn-success">Adicionar Tarefa</button>
            </div>
          </div>
        </>
      )}


      {ToDo && ToDo.length ? '' : 'Nenhuma tarefa adicionada'}

      {ToDo && ToDo
        .sort((a,b) => a.id > b.id ? 1:-1)
        .map( (task, index) => {
          return(
            <React.Fragment key={task.id}>

              <div className="col taskBg">

                <div className={task.status ? 'done' : ''}>
                  <span className="taskNumber">{index + 1}</span>
                  <span className="taskText">{index.title}</span>
                </div>

                <div className="iconsWrap">

                  <span title="Finalizado / Incompleto"
                    onClick={ (e) => markDone(task.id)}
                  ><FontAwesomeIcon icon={faCircleCheck}/></span>

                  {task.status ? null : (
                    <span title="Editar"
                    onClick={ ()=> setUpdateData({
                      id: task.id,
                      title: task.title,
                      status: task.status ? true : false
                    })}
                    ><FontAwesomeIcon icon={faPen}/></span>
                  )}


                  <span title="Deletar"
                  onClick={()=> deleteTask(task.id)}                  
                  ><FontAwesomeIcon icon={faTrashCan}/></span>
                </div>

              </div>

            </React.Fragment>
          )
        } )        
      }

    </div>
  );
}

export default App;
